import React from 'react';
import ReactDOM from 'react-dom';
import './Levels.css';
import Question from './Question.js';
import './Questions.json';

class Levels extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questionNum: 0}
        this.updateQuestionNum = this.updateQuestionNum.bind(this);   
    }
    componentDidMount() {
        for (var i=1; i<101; i++) {
            document.getElementById(i).onclick = function () {
                ReactDOM.render(<Question />, document.getElementById('root'));
            }
        }
    }
    updateQuestionNum(id) {
        this.setState({questionNum: id});
    }
    render () {
        var newButtons = [];
        for (var i=1; i<101; i++) {
            newButtons.push(<button key={i} type="button" id={i}>{i}</button>);
        }
        return (
            <div className="levels">
                <button type="button" className="leftArrow">&larr;</button>
                <div className="container">
                    <div className="buttonQuestions">
                        <h1>Levels</h1>
                        {newButtons}
                    </div>
                </div>
            </div>
        );
    }
}

export default Levels;
