import React from 'react';
import Levels from './Levels';
var obj = require("./Questions.json");

class Question extends React.Component {
    constructor(props){
        super(props);
        this.state = { questionNum: 1}
        this.updateQuestionNum = this.updateQuestionNum.bind(this);
    }
    updateQuestionNum(id) {
        this.setState({questionNum: id});
    }
    render() {
        var question = obj["question"+this.state.questionNum];
        return <Levels />
    }
}

export default Question;
