import React from 'react';
import './Question.css';
var obj = require("./Questions.json");

class Question extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleSubmit(e) {
        alert("Hello");
        e.preventDefault();
    }
    /*componentDidMount () {
        document.getElementById("ans").addEventListener("keyup", function(event) {
            event.preventDefault();
            if (event.keyCode === 13) {
                document.getElementById("ansSubmit").click();
            }
        });
    }*/
    render() {
        var getQuestion = obj["question"+this.props.questionNum];
        var output = []
        var individualClue = getQuestion.split("\n");
        for (var i=0; i<individualClue.length; i++) {
            output.push(<h3 key={i}>{individualClue[i]}</h3>);
        }
        return (
            <div className="display">
                <button type="button" className="leftArrow" onClick={() => document.location.reload()}>&larr;</button>
                <button type="button" className="rightArrow" disabled>&rarr;</button>
                <div className="container">
                    {/*<div className="question">
                        <h1>Level {this.props.questionNum}</h1>
                        {output}
                        <input type="text" id="ans" placeholder="Answer"></input>
                        <button type="submit" id="ansSubmit" onClick={() => alert("Hello")}>&uarr;</button>
                    </div>*/}
                    <form className="question" onSubmit={this.handleSubmit}>
                        <label>
                            <input type="text" placeholder="Answer" />
                        </label>
                        <input type="submit" value="&uarr;" />
                    </form>
                </div>
            </div>
        )
    }
}

export default Question;
