import React from 'react';
import './Question.css';
var obj = require("./Questions.json");
var answer = document.getElementById("ans");
class Question extends React.Component {
    constructor(props) {
        super(props);
        this.checkFunction = this.checkFunction.bind(this);
    }
    componentDidMount() {
    }
    checkFunction() {
        answer.addEventListener("keyup", function (e) {
            if (e.keyCode === 13) {
                e.preventDefault();
                document.getElementById("checkbtn").click();
            }
        })
        alert("Hello");
    }
    render() {
        var getQuestion = obj["question"+this.props.questionNum];
        var output = []
        var individualClue = getQuestion.split("\n");
        for (var i=0; i<individualClue.length; i++) {
            output.push(<h3 key={i}>{individualClue[i]}</h3>);
        }
        return (
            <div className="display">
                <button type="button" className="leftArrow" onClick={() => document.location.reload()}>&larr;</button>
                <button type="button" className="rightArrow" disabled>&rarr;</button>
                <div className="container">
                    <div className="question">
                        <h1>Level {this.props.questionNum}</h1>
                        {output}
                        <div className="answerInput">    
                            <input type="text" placeholder="Answer" id="ans"/>
                            <button type="submit" id="checkbtn" onClick={this.checkFunction()}>&uarr;</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Question;
