import React from 'react';
import ReactDOM from 'react-dom';
import './Levels.css';
import Question from './Question.js';
import './Questions.json';

var newButtons = [];
for (var i=1; i<101; i++) {
    newButtons.push(<button key={i} type="button" id={i} onClick={this.clickHandler(1)}>{i}</button>);
}
class Levels extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questionNum : 1}
        this.clickHandler = this.clickHandler.bind(this);
    }
    clickHandler() {
        this.setState({questionNum: element.props.id})
    }
    componentDidMount() {
        for (var element of newButtons){
            var id = element.props.id;
            console.log(id);
            document.getElementById(id).onclick = function () {
                ReactDOM.render(<React.StrictMode><Question questionNum={id} /></React.StrictMode>, document.getElementById('root'));
            }
        }
    }
    render () {
        return (
            <div className="levels">
                <button type="button" className="leftArrow">&larr;</button>
                <div className="container">
                    <div className="buttonQuestions">
                        <h1>Levels</h1>
                        <div className="buttons">
                            {newButtons}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Levels;
